<?php
// add a bas64 image in a PPTX created from scratch

require_once __DIR__ . '/../../../classes/CreatePptx.php';

$pptx = new CreatePptx();

$base64Image = 'data:image/png;base64,';

$position = array(
    'coordinateX' => 1400000,
    'coordinateY' => 5500000,
    'sizeX' => 1000000,
    'sizeY' => 1000000,
);
$pptx->addImage($base64Image, $position);

$pptx->savePptx(__DIR__ . '/example_addImage_3');