<?php
// replace image variables (placeholders) using a base64 image. The placeholder has been added to the alt text content

require_once __DIR__ . '/../../../classes/CreatePptx.php';

$pptx = new CreatePptxFromTemplate(__DIR__ . '/../../files/sample_template.pptx');

$imageBase64 = 'data:image/png;base64,';

// replace image in slides target
$pptx->replaceVariableImage(
    array(
        'VAR_IMAGE_PHPDOCX' => $imageBase64,
    )
);

$pptx->savePptx(__DIR__ . '/example_replaceVariableImage_4');